mapper.select:
SELECT cr.record_id, cr.reserve_id, cr.counselor_id, cu.user_name AS counselor_name, -- 关联用户表获取咨询师姓名 ri.student_id, su.user_name AS student_name, -- 关联用户表获取学生姓名 DATE_FORMAT(cr.consult_date, '%Y-%m-%d') AS consult_date, cr.student_performance, cr.consult_content, cr.advice_plan, cr.create_time, cr.update_time FROM consult_record cr -- 关联预约表获取学生ID INNER JOIN reservation_info ri ON cr.reserve_id = ri.reserve_id -- 关联用户表获取学生姓名 LEFT JOIN sys_user su ON ri.student_id = su.user_id -- 关联用户表获取咨询师姓名 LEFT JOIN sys_user cu ON cr.counselor_id = cu.user_id WHERE 1 = 1
AND ri.student_id = #{studentId}
AND cr.counselor_id = #{counselorId}
AND cr.consult_date = #{consultDate}
AND cr.consult_date >= #{startDate}
AND cr.consult_date <= #{endDate}
ORDER BY cr.consult_date DESC, cr.create_time DESC
LIMIT #{offset}, #{pageSize}

SELECT
COUNT(*)
FROM
consult_record cr
INNER JOIN reservation_info ri ON cr.reserve_id = ri.reserve_id
WHERE
1 = 1

    AND ri.student_id = #{studentId}


    AND cr.counselor_id = #{counselorId}


    AND cr.consult_date = #{consultDate}


    AND cr.consult_date >= #{startDate}


    AND cr.consult_date <= #{endDate}
